package com.cndroid.reactnativehotfixlib;

/**
 * Created by jinbangzhu on 03/01/2017.
 */
public class GameClientHotFixSetup {
    private static GameClientHotFixSetup ourInstance = new GameClientHotFixSetup();

    public static GameClientHotFixSetup getInstance() {
        return ourInstance;
    }

    private GameClientHotFixSetup() {
    }

    public void initial() {
        setExtraZipPath(getExtraRootPath() + "game_client.zip");
        setExtraGameClientPath(getExtraRootPath() + "game_client");
        setExtraInfoPath(getExtraGameClientPath() + "/info.json");
    }


    public String getExtraRootPath() {
        return extraRootPath;
    }

    public void setExtraRootPath(String extraRootPath) {
        this.extraRootPath = extraRootPath;
    }

    public String getExtraZipPath() {
        return extraZipPath;
    }

    private void setExtraZipPath(String extraZipPath) {
        this.extraZipPath = extraZipPath;
    }

    public String getExtraInfoPath() {
        return extraInfoPath;
    }


    public String getExtraGameClientPath() {
        return extraGameClientPath;
    }

    private void setExtraGameClientPath(String extraGameClientPath) {
        this.extraGameClientPath = extraGameClientPath;
    }
    private void setExtraInfoPath(String extraInfoPath) {
        this.extraInfoPath = extraInfoPath;
    }

    /**
     * local bundle path where is index.android.bundle
     */
    private String extraRootPath;
    private String extraZipPath;
    private String extraInfoPath;
    private String extraGameClientPath;
}
